mapper.insert:
insert into repairinfo(brcode,stuid,stutel,type,content,subtime)
select brcode,#{stuid},#{stutel},#{type},#{content},#{subtime}
from allocationinfo ai
where ai.stuid = #{stuid}

mapper.update:
update repairinfo


        stutel = #{stutel},


        type = #{type},


        content = #{content}


where id = #{id}

mapper.select:
select * from messageboard


        and type = #{type}


        and (brcode like concat("%",#{brcode},"%")


        or brcode like #{brarea})


order by id desc
limit #{start},#{size}

mapper.delete:
delete from messageboard
where id in

    #{id}

mapper.select:
select * from leaveinfo


        stuid = #{stuid}


order by id desc

mapper.update:
update leaveinfo


        got = #{got},


        time = #{time},


        dest = #{dest},


        ectel = #{ectel}


where id = #{id}

mapper.select:
select * from stayinfo


        stuid = #{stuid}


order by id desc

mapper.update:
update stayinfo


        reason = #{reason},


        time = #{time},


        opinion = #{opinion}


where id = #{id}

mapper.select:
select * from backtoscinfo
where stuid = #{stuid}
order by id desc

mapper.update:
update allocationinfo
